import React, {Component} from 'react';
import axios from 'axios';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle
  } from 'reactstrap';
import {
    Navbar,
    Input,
    Button,
    InputGroup,
    InputGroupAddon,
    Container,
    Col,
    Row,
    Form
} from 'reactstrap';
import { MdSearch} from 'react-icons/md'
class Home extends Component {
    state ={
        meteoro: []
    }

    meteoroDaPaixao = async (evento) =>{
        evento.preventDefault();

         const form = evento.target;
        const InputGroup = form.children[0];
        const input = InputGroup.children[0];

        //const seguidores = await axios(`https://api.github.com/users/${input.value}/followers`);
        const meteoro = await axios(`https://api.nasa.gov/planetary/apod?date=${input.value}&api_key=hXs5BNSzi1nGxwrz5STUyei0xcag6MsDBBHPDo3b`);


        this.setState({meteoro: [meteoro.data]});

        
        

  


    }
    render() {
        return (
            <>
                <Navbar color="dark" fixed="bottom">
                    <Container className="d-flex justify-content-center">
                        <Col xs="12" md="6">
                            <Form onSubmit = {this.meteoroDaPaixao}>
                                <InputGroup>
                                    <Input />
                                    <InputGroupAddon addonType="append">
                                        <Button color="danger">
                                        <MdSearch size="20" />
                                        </Button>            
                                    </InputGroupAddon>
                                </InputGroup> 
                            </Form>
                        </Col>
                    </Container>
                </Navbar>

                <Container>
                    <Row>

                {this.state.meteoro.map((meteoro) =>(
                    <Col xs="12" md="4">
                    <Card>
                    <CardImg top width="100%" src={meteoro.url} alt={meteoro.title} />
                    <CardBody>
                <CardTitle>{meteoro.title}</CardTitle>
                <CardSubtitle>{meteoro.date}</CardSubtitle>
                <CardText>{meteoro.explanation}</CardText>
                     
                    </CardBody>
                  </Card>
                  </Col>
                ))}
                </Row>
                </Container>

            </>
        )
    }
}
export default Home;
