import React, {Component} from 'react';
import axios from 'axios';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle
  } from 'reactstrap';
import {
    Navbar,
    Input,
    Button,
    InputGroup,
    InputGroupAddon,
    Container,
    Col,
    Row,
    Form,
    Spinner

} from 'reactstrap';
import { MdSearch} from 'react-icons/md'
class Home extends Component {
    state ={
        carregando: false,
        meteoro: []
    }

    meteoroDaPaixao = async (evento) =>{
        evento.preventDefault();

        this.setState({carregando: true});

         const form = evento.target;
        const InputGroup = form.children[0];
        const input = InputGroup.children[0];

        //const seguidores = await axios(`https://api.github.com/users/${input.value}/followers`);
        const meteoro = await axios(`https://api.nasa.gov/planetary/apod?date=${input.value}&api_key=hXs5BNSzi1nGxwrz5STUyei0xcag6MsDBBHPDo3b`);


        this.setState({meteoro: [meteoro.data, ...this.state.meteoro], carregando: false});

    }
    render() {
        return (
            <>
                <Navbar color="dark" fixed="bottom">
                    <Container className="d-flex justify-content-center">
                        <Col xs="12" md="6">
                            <Form onSubmit = {this.meteoroDaPaixao}>
                                <InputGroup>
                                    <Input type="date"/>
                                    <InputGroupAddon addonType="append">
                                        <Button color="danger">
                                            {this.state.carregando ? (<Spinner color="ligth" size="sm"/>) : (<MdSearch size="20" />) }
                                      
                                        </Button>            
                                    </InputGroupAddon>
                                    
                                </InputGroup> 
                            </Form>
                        </Col>
                    </Container>
                </Navbar>

                <Container className="mt-3 mb-5" >
                    <Row>

                {this.state.meteoro.map((meteoro) =>(
                    <Col className="d-flex" xs="12" md="4">
                    <Card className="text-white mb-2" color="dark">
                    <CardImg top width="100%" height="30%" src={meteoro.url} alt={meteoro.title} />
                    <CardBody>
                <CardTitle className ="h3">{meteoro.title}</CardTitle>
                <CardSubtitle>{meteoro.date.split('-').reverse().join('/')}</CardSubtitle>
                <CardText className=" text-center">{meteoro.explanation}</CardText>
                     
                    </CardBody>
                  </Card>
                  </Col>
                ))}
                </Row>
                </Container>

                {this.state.carregando && (
                <Container className="h-100 d-flex flex-cojustify-content-center align-items-center">
                    <Spinner color="dark" size="lg"/>
                    <span>CArregando...</span>
                    </Container>

                )}


            </>
        )
    }
}
export default Home;
